generated.css:
* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
  list-style: none;
  text-decoration: none;
  color: #000;
}
html {
  font-family: 'Open Sans', sans-serif;
  background-color: #7b68ee;
  font-size: 15px;
  text-align: center;
  padding: 0 0 1rem 0;
}
h1 {
  margin: 1rem;
}
form {
  background-color: #e6e6fa;
  border-radius: 3px;
  width: 80vw;
  margin: 0 auto;
  padding: 1rem 2rem;
}
form .description {
  margin: 0 1rem;
}
form .row {
  margin: 1rem 0;
  display: flex;
  flex-direction: column;
}
@media (min-width: 520px) {
  form .row {
    flex-direction: row;
  }
}
form .row__left-side {
  text-align: left;
}
@media (min-width: 520px) {
  form .row__left-side {
    flex: 1;
    text-align: right;
    margin: 0 1rem 0 0;
  }
}
form .row__left-side .label {
  padding: 0.5rem 0;
}
@media (min-width: 520px) {
  form .row__right-side {
    flex: 1;
  }
}
form .row__right-side .input {
  width: 100%;
  padding: 0.5rem;
  border: 1px solid #808080;
}
form .row__right-side select {
  width: 100%;
  padding: 0.5rem;
  border: 1px solid #808080;
}
form .row__right-side ul {
  text-align: left;
}
form .row__right-side textarea {
  width: 100%;
  padding: 0.5rem 0 2rem 0.5rem;
  border-radius: 3px;
  border: 1px solid #808080;
  resize: vertical;
}
form button {
  padding: 0.5rem 1rem;
  border: none;
  background-color: #d8bfd8;
  border-radius: 3px;
  cursor: pointer;
  transition: background-color 0.3s ease-in-out;
}
form button:hover {
  background-color: #dda0dd;
}
